### Multi-step ECB encryption in blocking mode # #include<ti/drivers/AESECB.h>
#include<ti/drivers/cryptoutils/cryptokey/CryptoKeyPlaintext.h>

#defineAES_BLOCK_SIZE16//bytes

...

AESECB_Handlehandle;
CryptoKeycryptoKey;
int_fast16_tencryptionResult;
int_fast16_tsetupEncryptionResult;
int_fast16_tfinalizeEncryptionResult;
uint8_tplaintext[]={0x6b,0xc1,0xbe,0xe2,0x2e,0x40,0x9f,0x96,
0xe9,0x3d,0x7e,0x11,0x73,0x93,0x17,0x2a,
0xae,0x2d,0x8a,0x57,0x1e,0x03,0xac,0x9c,
0x9e,0xb7,0x6f,0xac,0x45,0xaf,0x8e,0x51};
uint8_tciphertext[sizeof(plaintext)];
uint8_tkeyingMaterial[16]={0x2b,0x7e,0x15,0x16,0x28,0xae,0xd2,0xa6,
0xab,0xf7,0x15,0x88,0x09,0xcf,0x4f,0x3c}

handle=AESECB_open(0,NULL);

if(handle==NULL){
//handleerror
}

CryptoKeyPlaintext_initKey(&cryptoKey,keyingMaterial,sizeof(keyingMaterial));

setupEncryptionResult=AESECB_setupEncrypt(handle,&cryptoKey);
if(setupEncryptionResult!=AESECB_STATUS_SUCCESS){
//handleerror
}

AESECB_Operationoperation;
AESECB_Operation_init(&operation);

//Noneedtosetoperation.keyformulti-stepoperations.
operation.input=plaintext;
operation.output=ciphertext;
//Inputlengthmustbeanon-zeromultipleofblock-size(16bytes)forcalling
//#AESECB_addData().Theuserorapplicationshouldtakecareofnecessarypadding
//ifthefinalblockofdataisbeingaddedfortheentiresegmentedoperation.
operation.inputLength=AES_BLOCK_SIZE;

encryptionResult=AESECB_addData(handle,&operation);
if(encryptionResult!=AESECB_STATUS_SUCCESS){
//handleerror
}

//Noneedtosetoperation.keyformulti-stepoperations.
operation.input=plaintext+AES_BLOCK_SIZE;
operation.output=ciphertext+AES_BLOCK_SIZE;
//Inputlengthmusteitherbeanon-zeromultipleofblock-size(16bytes)
//forcalling#AESECB_finalize(),oritcouldbezeroincaseoffinalizingwithout
//anymoredata.Theuserorapplicationshouldtakecareofnecessarypadding
//forthelastblockofdata.
operation.inputLength=AES_BLOCK_SIZE;

finalizeEncryptionResult=AESECB_finalize(handle,&operation);
if(finalizeEncryptionResult!=AESECB_STATUS_SUCCESS){
//handleerror
}

//Theresultantciphertextshouldbe:
//0x3a,0xd7,0x7b,0xb4,0x0d,0x7a,0x36,0x60,
//0xa8,0x9e,0xca,0xf3,0x24,0x66,0xef,0x97,
//0xf5,0xd3,0xd5,0x85,0x03,0xb9,0x69,0x9d,
//0xe7,0x85,0x89,0x5a,0x96,0xfd,0xba,0xaf


AESECB_close(handle);

}
